import React from 'react';
import PropsTypes from 'prop-types';

const PostCardContent = ({ postData }) => {
  return (
    <>
      {postData.split(/(\<[^\>]+\>)/g).map((v) => {
        if (v.match(/\<[^\>]+\>/)) {
          return (
            <strong key={v} style={{color: 'black'}}>{v}</strong>
          )
        }
        v.split('\n').map(value => {
          console.log(value);
          return (
            <p key={value}>{value}</p>
          )
        })
      })}
    </>
  )
}

export default PostCardContent;
