import React from 'react';
import { useSelector } from 'react-redux'


const DateCell = ({date}) => {

  const { mainPosts } = useSelector(state => state.post);
  console.log(mainPosts)
  const postsDate = mainPosts.map(v => String(v.createdAt).substring(0,10));
  console.log(postsDate)
  const toDay = Date().substring(0,15);
  const calendarCell = String(date._d).substring(0,15);

  return (
    <>
      {calendarCell == toDay ?
      <div style={{ backgroundColor:'red', width: '65px', height: '3px', marginTop: '0px'}}></div>
      :
      null
      } 
    </>
  )
}

export default DateCell;
