import React from 'react';
import { useSelector } from 'react-redux'

//postsDate 2020-01-09
//calendatCell Jan 09 2020


const DateCell = ({date}) => {

  const { mainPosts } = useSelector(state => state.post);
  const postsDate = mainPosts.map(v => String(v.createdAt).substring(0,10));
  let calendarCell = String(date._d).substring(4,15);
  const month = ["None","Jan","Feb","Mar","Apr","May","Jun","Jul","Aug", "Sep","Oct","Nov","Dec"];

  for(var i in calendarCell) {
    for(var j in month) {
      if(i.substring(0,3) == j) {
        calendarCell = calendarCell.replace(j, month.indexOf(j))
      }
    }
  }
  console.log(calendarCell);
  
  
  // for(i in postsDate) {
  //   if (i.substring(0,4) == calendarCell.substring(7,11) && i.substring(6,8) == calendarCell.substring(4,6)) {

  //   }
  // }

  return (
    <>
      {calendarCell ?
      <div style={{ backgroundColor:'red', width: '65px', height: '3px', marginTop: '0px'}}></div>
      :
      null
      } 
    </>
  )
}

export default DateCell;
