import React from 'react';
import PropsTypes from 'prop-types';
import { Typography } from 'antd';

const { Paragraph } = Typography;

const PostCardContent = ({ postData }) => {

  const content = postData.content;

  const onChange = (str) => {

  }

  
  return (
    <>
      {content.split(/(오늘 한 일|문제점|내일 할 일)/g).map((v, i) => {
          if (v.match(/오늘 한 일|문제점|내일 할 일/)) {
            return (
              <strong key={i} style={{color: 'black', fontSize: '20px'}}>{v}</strong>
            )
          }
          return v.split('\n').map((value, i) => {
            return <Paragraph editable={{ onChange: onChange}} key={i}>{value}</Paragraph>
          })
        })
      }
    </>
  )
}

export default PostCardContent;
