import React from 'react';
import Head from 'next/head';
import PropTypes from 'prop-types';
import withRedux from 'next-redux-wrapper';
import { applyMiddleware, compose, createStore } from 'redux';
import { Provider } from 'react-redux';
import withReduxSaga from 'next-redux-saga';
import { composeWithDevTools } from 'redux-devtools-extension';
import AppLayout from '../components/AppLayout';
import reducer from '../reducer';
import createSagaMiddleware from 'redux-saga';
import rootSaga  from '../sagas';


const Restropect = ({ Component, store }) => {
  return (
    <>
      <Provider store={store}>
        <Head>
          <title>restropect</title>
          <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.25.3/antd.css" />
        </Head>
        <AppLayout>
          <Component />
        </AppLayout>
      </Provider>
    </>
  );
}

Restropect.propTypes = {
  Component: PropTypes.elementType,
  store: PropTypes.object,
};


Restropect.getIntialProps = async (context) => {
  const { ctx, Component } = context;
  const pageProps = {};
  if (Component.getIntialProps) {
    pageProps = await Component.getIntialProps(ctx);
  }
  const state = ctx.store.getState();
  if (!state.user.userId) {
    context.store.dispatch({
      type: LOAD_USER_REQUEST,
    });
  }

  return pageProps;
}

const configureStore = (initialState) => {
  const sagaMiddleware = createSagaMiddleware();
  const middlewares = [sagaMiddleware];
  const enhancer = compose(composeWithDevTools(applyMiddleware(...middlewares)));
  const store = createStore(reducer, initialState, enhancer);
  store.sagaTask = sagaMiddleware.run(rootSaga);
  return store;
};


export default withRedux(configureStore)(withReduxSaga(Restropect));
