import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import PropsTypes from 'prop-types';
import { Menu, Input, Row, Col } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { LOG_OUT_REQUEST } from '../reducer/user';
const { SubMenu } = Menu;

const AppLayout = ({ children }) => {

  const [current, setCurrent] = useState('home');
  const { user } = useSelector(state => state.user);
  const dispatch = useDispatch();

  const handleClick = (e) => {
    setCurrent(e.key);
  };

  useEffect(() => {
    if (current === 'logout') {
      dispatch({
        type: LOG_OUT_REQUEST
      });
    }
    setCurrent('');
  }, [current])

  return (
    <>
      <Menu mode="horizontal" selectedKeys={current} onClick={handleClick} style={{ backgroundColor: '#00001a'}}>
        <Menu.Item key="home" style={{ color: 'white'}}><Link href="/"><a>restropect</a></Link></Menu.Item>
        <Menu.Item key="search">
          <Input.Search enterButton style={{ verticalAlign: 'middle' }} />
        </Menu.Item>
        <SubMenu title={ <span className="submenu">{user.nick ? user.nick : ''}</span>} style={{ float: 'right' }}>
          <Menu.Item key="private">개인정보</Menu.Item>
          <Menu.Item key="logout">로그아웃</Menu.Item>
        </SubMenu>
      </Menu>
      <Row gutter={8}>
        <Col xs={24} md={20}>
          {children}
        </Col>
        <Col xs={24} md={4}>
          달력예정
        </Col>
      </Row>
    </>
  )
}

AppLayout.propTypes = {
  children: PropsTypes.node,
}

export default AppLayout;
