import React, { useCallback } from 'react';
import { Button, Form, Input } from 'antd';
import Link from 'next/link';
import { useDispatch } from 'react-redux';
import { useInput } from '../pages/signup';
import { LOG_IN_REQUEST } from '../reducer/user';


const Login = () => {
  const [userId, onChangeId] = useInput('');
  const [password, onChangePassword] = useInput('');

  const dispatch = useDispatch();

  const onSubmitForm = useCallback((e) => {
    e.preventDefault();
    dispatch({
      type: LOG_IN_REQUEST,
      data: {
        userId,
        password
      }
    });
  }, [userId, password]);

  return (
    <div style={{ position: 'absolute', left: '50%', top: '50%', marginTop: '50px', padding:'30px', border: '1px solid #ccc', borderTopLeftRadius: '4px' }}>
      <Form onSubmit={onSubmitForm}>
        <div>
          <label htmlFor="user-id">아이디</label>
          <br />
          <Input name="user-id" value={userId} onChange={onChangeId} style={{width: '250px'}} required />
        </div>
        <br />
        <div>
          <label htmlFor="user-password">비밀번호</label>
          <br />
          <Input name="user-password" value={password} onChange={onChangePassword} type="password" style={{width: '250px'}} required />
        </div>
        <br />
        <div style={{ marginTop: '10px' }}>
          <Button type="primary" htmlType="submit" style={{marginRight:'6px', width: '120px' }}>로그인</Button>
          <Link href="/signup"><a><Button style={{ width: '120px' }}>회원가입</Button></a></Link>
        </div>
      </Form>
    </div>
  );
};

export default Login;
